import argparse
import os
import sys
import math
from find_start_and_end_of_cds import readFastaFile

cdna_filename=sys.argv[1]
new_cdna_filename=cdna_filename+".temp"
cds_filename=sys.argv[2]
new_cds_filename=cds_filename+".temp"
protein_filename=sys.argv[3]
new_protein_filename=protein_filename+".temp"

replace_cdna={"HORVU2Hr1G037750.1":"",
              "HORVU2Hr1G037750.2":"",
              "HORVU2Hr1G037750.3":"",
              "HORVU2Hr1G037750.7":""}

replace_cds= {"HORVU2Hr1G037750.1":"",
              "HORVU2Hr1G037750.2":"",
              "HORVU2Hr1G037750.3":"",
              "HORVU2Hr1G037750.7":""}

replace_prot={"HORVU2Hr1G037750.1":"",
              "HORVU2Hr1G037750.2":"",
              "HORVU2Hr1G037750.3":"",
              "HORVU2Hr1G037750.7":""}

def writeFastaFile(data,filename):
    fhw=open(filename,"w")
    for transcript in data:
        fhw.write(">"+transcript+"\n"+data[transcript]+"\n")
    fhw.close()

def main():
    cdna=readFastaFile(cdna_filename)
    cds=readFastaFile(cds_filename)
    prot=readFastaFile(protein_filename)
    for transcript in replace_cdna:
        cdna[transcript]=replace_cdna[transcript]
    for transcript in replace_cds:
        cds[transcript]=replace_cds[transcript]
    for transcript in replace_prot:
        prot[transcript]=replace_prot[transcript]
    writeFastaFile(cdna,new_cdna_filename)
    writeFastaFile(cds, new_cds_filename)
    writeFastaFile(prot, new_protein_filename)
    cmd="mv "+new_cdna_filename+" "+cdna_filename
    os.system(cmd)
    cmd="mv "+new_cds_filename+" "+cds_filename
    os.system(cmd)
    cmd="mv "+new_protein_filename+" "+protein_filename
    os.system(cmd)

if __name__ == "__main__":
    main()